import {useState} from "react";

// Testing
function InputHandler(text) {
	text = text.toLowerCase();
	var verse_array = [];
	var lineReader = require('linebyline').createInterface({
	input: require('fs').createReadStream('../text_files/text.txt')
	});

	lineReader.on('line', function (line) {
        verse_array.push(line.toLowerCase());
	});

	lineReader.on('close', function(){
        for(var i = 0; i < verse_array.length; i++){
             if(verse_array[i].includes(text)){
				console.log("Found in line " + i + "!");
            }
        }
	});

	console.debug(text.toLowerCase());
}

function SearchBar() {
	const [search_term, SetSearchTerm] = useState("");
	return (
		<div>
			<input type="search" id="bible_search" name="bible_search" onChange={event => {SetSearchTerm(event.target.value)}}></input>
			<button value={search_term} onClick={e => InputHandler(e.target.value)}>Search</button>
		</div>
		);
}
export default SearchBar;
