import {useState} from "react";

// Testing
function InputHandler(text) {
	text = text.toLowerCase();

	/*
	var fs = require('fs');
	fs.readFile('../text_files/text.txt', function(err, data) {
		if (err) {
			console.debug("butt");
			return;
		}
		var verse_array = data.toString().toLowerCase().split("\n");
		for (var verse in verse_array) {
             if(verse.includes(text)){
				console.log(verse);
            }
		}
	});
	*/
	let file_reader;
	file_reader = new FileReader();
	//file_reader.onloadend = handleFileRead;
	file_reader.readAsText(new File([""], "text_files/text.txt"));

	console.debug(file_reader.result);
}

function SearchBar() {
	const [search_term, SetSearchTerm] = useState("");
	return (
		<div>
			<input type="search" id="bible_search" name="bible_search" onChange={event => {SetSearchTerm(event.target.value)}}></input>
			<button value={search_term} onClick={e => InputHandler(e.target.value)}>Search</button>
		</div>
		);
}
export default SearchBar;
