let lc_search = "";

function SearchBarOnClick() {
	// Replace with code to search a verse.
	console.debug("Searched term: " + lc_search);
}

function InputHandler(e) {
	lc_search = e.target.value.ToLowerCase();
}

function SearchBar() {
	return (
		<div>
			<input type="search" id="bible_search" name="bible_search" onChange={InputHandler}></input>
			<button onClick={SearchBarOnClick}>Search</button>
		</div>
		);
}
export default SearchBar;
