	console.debug("Searched term: " + lc_search);
}

function SearchBar() {
	return (
		<div>
			<input type="search" id="bible_search" name="bible_search" onChange={InputHandler(target.value)}></input>
			<button onClick={SearchBarOnClick}>Search</button>
		</div>
		);
}
export default SearchBar;
